import React,{ Component} from 'react';
import DataTable from 'react-data-table-component';
import orderBy from 'lodash/orderBy';
import 'react-dropdown/style.css';
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import Axios from 'axios';
import Select from 'react-select';

//#region Constants

const data = [{ title: '12 Angry Men', director: 'Sidney Lumet', year: '1957' },
                { title: 'A Separation', director: 'Asghar Farhadi', year: '2011' },
                { title: 'Alice in Wonderland', director: 'Tim Burton', year: '2010' },
                { title: 'Alive', director: 'Frank Marshall', year: '1993' },
                { title: 'All Quiet on the Western Front', director: 'Lewis Milestone', year: '1930' }, 
                { title: 'Before Sunrise', director: 'Richard Linklater', year: '1995' }, 
                { title: 'Chinatown', director: 'Roman Polanski', year: '1974' }, 
                { title: 'Das Boot', director: 'Wolfgang Petersen', year: '1981' }, 
                { title: 'I-See-You.Com', director: 'Eric Steven Stahl', year: '2006' }
            ];
const columns = [
    {name: 'Attribute',selector: 'title',sortable: true,},
    {name: '01:00:00',selector: '01:00',sortable : true},
    {name: '02:00:00',selector: '02:00',sortable : true},
    {name: '03:00:00',selector: '03:00',sortable : true},
    {name: '04:00:00',selector: '04:00',sortable : true},
    {name: '05:00:00',selector: '05:00',sortable : true},
    {name: '06:00:00',selector: '06:00',sortable : true},
    {name: '07:00:00',selector: '07:00',sortable : true},
    {name: '08:00:00',selector: '08:00',sortable : true},
    {name: '09:00:00',selector: '09:00',sortable : true},
    {name: '10:00:00',selector: '10:00',sortable : true},
    {name: '11:00:00',selector: '11:00',sortable : true},
    {name: '12:00:00',selector: '12:00',sortable : true},
    {name: '13:00:00',selector: '13:00',sortable : true},
    {name: '14:00:00',selector: '14:00',sortable : true},
    {name: '15:00:00',selector: '15:00',sortable : true},
    {name: '16:00:00',selector: '16:00',sortable : true},
    {name: '17:00:00',selector: '17:00',sortable : true},
    {name: '18:00:00',selector: '18:00',sortable : true},
    {name: '19:00:00',selector: '19:00',sortable : true},
    {name: '20:00:00',selector: '20:00',sortable : true},
    {name: '21:00:00',selector: '21:00',sortable : true},
    {name: '22:00:00',selector: '22:00',sortable : true},
    {name: '23:00:00',selector: '23:00',sortable : true},
    {name: '24:00:00',selector: '24:00',sortable : true}    
  ];
 
const handleSort = (column, sortDirection) => console.log(column.selector, sortDirection);

const customSort = (rows, field, direction) => {
    const handleField = row => {
      if (row[field]) {
        return row[field].toLowerCase();
      }
  
      return row[field];
    };
  
    return orderBy(rows, handleField, direction);
  };


const hours =[
  {value : '01:00:00', label: '01:00:00'},{value : '02:00:00', label: '02:00:00'},
  {value : '03:00:00', label: '03:00:00'},{value : '04:00:00', label: '04:00:00'},
  {value : '05:00:00', label: '05:00:00'},{value : '06:00:00', label: '06:00:00'},
  {value : '07:00:00', label: '07:00:00'},{value : '08:00:00', label: '08:00:00'},
  {value : '09:00:00', label: '09:00:00'},{value : '10:00:00', label: '10:00:00'},
  {value : '11:00:00', label: '11:00:00'},{value : '12:00:00', label: '12:00:00'},
  {value : '13:00:00', label: '13:00:00'},{value : '14:00:00', label: '14:00:00'},
  {value : '15:00:00', label: '15:00:00'},{value : '16:00:00', label: '16:00:00'},
  {value : '17:00:00', label: '17:00:00'},{value : '18:00:00', label: '18:00:00'},
  {value : '19:00:00', label: '19:00:00'},{value : '20:00:00', label: '20:00:00'},
  {value : '21:00:00', label: '21:00:00'},{value : '22:00:00', label: '22:00:00'},
  {value : '23:00:00', label: '23:00:00'},{value : '24:00:00', label: '24:00:00'}
]
//#endregion

class LPDT extends Component{

    constructor(props){
      super(props);
      this.state = {
        startDate: new Date(),
        devices :[],
        id : "",
        sdate : "",
        shour : "",
        ehour : "",
        rowdata :[
          {
            name: 'Attribute',
            selector: 'title',
            sortable: true,
          }
        ]
      };
      this._onDeviceSelect = this._onDeviceSelect.bind(this);
      this._onhourSelect = this._onhourSelect.bind(this);
      this._onEndHourSelect = this._onEndHourSelect.bind(this);
      this.handleDateChange = this.handleDateChange.bind(this);
      this._onButtonClick = this._onButtonClick.bind(this);
    }

    processResponse(response){
      console.log(response[0]);
    }
    
    //#region Events 
    getParsedDate(date){
      date = String(date).split(' ');
      var year = String(date[3]).split('-');
      var month = new Date(Date.parse(String(date[1]).split(':') +" 1, 2012")).getMonth()+1;
      if(month< 10) month = "0"+month;
      var day = String(date[2]).split(':');
      return year+"-"+month+"-"+day+" ";
    }

    _onDeviceSelect(event){
      this.setState({
        id : event.value
      })
    }

    _onhourSelect(event){
      this.setState({
        shour : event.label,
      });
    }

    _onEndHourSelect(event){
      this.setState({
        ehour : event.label,
      });
    }

    handleDateChange = date => {
      var s = this.getParsedDate(date);
      console.log(s);
      this.setState({
        startDate : date,
        sdate : s
      });
      console.log(this.state.sdate );
    };

    _onButtonClick(event) {
      console.log("On Button Click");
      Axios.post("http://localhost:8090/getProfile",
      {
          headers: {
              'Access-Control-Allow-Origin': '*',
            },
      },
      {
        data:{
            _id:this.state.id,
            _startDate: this.state.sdate+this.state.shour+".000",
            _endDate: this.state.sdate+this.state.ehour+".000"
        }
      },
      {
          withCredentials : true
      }
      ).then(response=>{
          // console.log(response.data)
          this.processResponse(response.data);
      });
        
    }


    componentDidMount() {
      Axios.get("http://localhost:8090/getDevices",
      {
          headers: {
              'Access-Control-Allow-Origin': '*',
            },
      },
      {
          withCredentials : true
      }
      ).then(response=>{
          console.log(response.data);
          this.setState({
            devices : response.data
          })
      });
    }
    //#endregion

    render() {
        return (
          <div>
            <div>
              <table>
                <tbody>
                  <tr>
                    <td><label>Device ID:  </label></td>
                    <td><span> &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></td>
                    {/* <td>
                      <Dropdown options={this.state.devices} onChange={this._onDeviceSelect} placeholder="Select an option" />
                    </td>  */}
                    <td>
                      <Select
                        onChange={this._onDeviceSelect}
                        options={this.state.devices}
                        placeholder = "Select Device"
                      />
                    </td>
                  </tr>
                  <tr>
                  <td><label>Select Date:  </label></td>
                    <td><span> &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></td>
                    <td><DatePicker selected={this.state.startDate} onChange={this.handleDateChange}/></td>
                  </tr>
                  <tr>
                    <td><label>From:  </label></td>
                    <td><span> &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></td>
                    <td>
                      {/* <Dropdown options={hours} onChange={this._onhourSelect} placeholder="Select an option" /> */}
                      <Select
                        onChange={this._onhourSelect}
                        options={hours}
                        placeholder = "Select Time"
                      />
                    </td>
                  </tr>
                  <tr>
                    <td><label> To:  </label></td>
                    <td><span> &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></td>
                    <td>
                      {/* <Dropdown options={hours} onChange={this._onEndHourSelect} placeholder="Select an option" /> */}
                      <Select
                        onChange={this._onEndHourSelect}
                        options={hours}
                        placeholder = "Select Time"
                      />
                    </td>
                  </tr>
                  <tr>
                    <td>
                      <button type="submit" onClick={this._onButtonClick} >
                            Submit
                        </button> 
                    </td>
                  </tr>
                </tbody> 
              </table>
            </div>

            <div align="left">
                <DataTable
                  title="Load Profile Data"
                  columns={columns}
                  data={data}
                  fixedHeader
                  fixedHeaderScrollHeight="300px"
                  pagination
                  onSort={handleSort}
                  sortFunction={customSort}
              />
            </div>
          </div>
        )
      }
};

export default LPDT;
