import React,{ Component} from 'react';
import DataTable from 'react-data-table-component';

const data = [{ id: 1, director: 'Conan the Barbarian', year: '1982' } ];
const columns = [
    {
      name: 'Title',
      selector: 'title',
      sortable: true,
    },
    {
      name: 'Director',
      selector: 'director',
      sortable: true,
    },
    {
      name: 'Year',
      selector: 'year',
      sortable: true,
    },
  ];
 
const handleSort = (column, sortDirection) => console.log(column.selector, sortDirection);

class LPDT extends Component{
    
    render() {
        return (
          <DataTable
            title="Arnold Movies"
            columns={columns}
            data={data}
            fixedHeader
            fixedHeaderScrollHeight="300px"
            pagination
            onSort={handleSort}
            sortFunction={customSort}
          />
        )
      }
};

export default LPDT;
