import React, { Component } from "react";
import Home from "./home";

class Login extends Component {

    constructor(props){
        super(props);
        this.state = {
            showComponent: false,
            redirect : false,
            email: "",
            password: ""
          };
          this._onButtonClick = this._onButtonClick.bind(this);
          this.handleEmailChange = this.handleEmailChange.bind(this);
          this.pwdChange = this.pwdChange.bind(this);
    }

    handleEmailChange(event) {
        this.setState({
          email: event.target.value,
        });
    }

    pwdChange(event) {
        this.setState({
            password: event.target.value,
        });
    }

    _onButtonClick() {
        // console.log("In func");
        // const response =  fetch('http://localhost:8085/users',{
        //     method: 'GET',
        //     headers: {
        //       'Content-Type': 'application/json'
        //     }
        // });
        // const myJson =  response.json();
        // console.log(myJson);
        fetch("http://localhost:8085/users")
      .then(res => res.json())
      .then(
        (result) => {
            console.log(result.items)
          this.setState({
            isLoaded: true,
            items: result.items
          });
        },
        // Note: it's important to handle errors here
        // instead of a catch() block so that we don't swallow
        // exceptions from actual bugs in components.
        (error) => {
          this.setState({
            isLoaded: true,
            error
          });
        }
      )
        this.setState({
          showComponent: true,
        }); 
    }

    render() {
        return (
            <div>
                {this.state.showComponent ? 
                    <div>
                        <Home/>
                    </div> :(
                    <form>
                        <h3>Sign In</h3>

                        <div className="form-group">
                            <label>Email address</label>
                            <input type="email" className="form-control" placeholder="Enter email" onChange={this.handleEmailChange}/>
                        </div>

                        <div className="form-group">
                            <label>Password</label>
                            <input type="password" className="form-control" placeholder="Enter password" onChange={this.pwdChange} />
                        </div>

                        <div className="form-group">
                            <div className="custom-control custom-checkbox">
                                <input type="checkbox" className="custom-control-input" id="customCheck1" />
                                <label className="custom-control-label" htmlFor="customCheck1">Remember me</label>
                            </div>
                        </div>
                        <button type="submit" className="btn btn-primary btn-block" onClick={this._onButtonClick} >
                            Submit
                        </button>  

                        <p className="forgot-password text-right">
                            <a href="/">Forgot password?</a>
                        </p>

                    </form>)
                }  
            </div>
        );
    }
}

export default Login;
