import React, { Component } from "react";
import Home from "./home";
import { Link, Switch, Router } from "react-router-dom";
import RangeBarChart from "./views/column charts/Range Bar Chart";

class Login extends Component {

    constructor(props){
        super(props);
        this.state = {
            showComponent: false,
          };
          this._onButtonClick = this._onButtonClick.bind(this);
    }

    _onButtonClick() {
        this.setState({
          showComponent: true,
        });
    }
    render() {
        return (
            <div>
                {this.state.showComponent ?<Home/> :(
                    <form>
                        <h3>Sign In</h3>

                        <div className="form-group">
                            <label>Email address</label>
                            <input type="email" className="form-control" placeholder="Enter email" />
                        </div>

                        <div className="form-group">
                            <label>Password</label>
                            <input type="password" className="form-control" placeholder="Enter password" />
                        </div>

                        <div className="form-group">
                            <div className="custom-control custom-checkbox">
                                <input type="checkbox" className="custom-control-input" id="customCheck1" />
                                <label className="custom-control-label" htmlFor="customCheck1">Remember me</label>
                            </div>
                        </div>
                                {/* <button type="submit" className="btn btn-primary btn-block" onClick={this._onButtonClick} >
                                </button>     */}
                                    <Link to={'/topology'} className="nav-link" onClick={this._onButtonClick}> Submit </Link>
                                    <div className="auth-wrapper">
                                        <div className="auth-inner">
                                        <Switch>
                                            <Route path='/topology' component={RangeBarChart} />
                                        </Switch>
                                        </div>
                                    </div>

                                <p className="forgot-password text-right">
                            <a href="#">Forgot password?</a>
                        </p>

                    </form>)
                }  
            </div>
        );
    }
}

export default Login;
