import React,{ Component} from 'react';
import RangeBarChart from "./views/column charts/Range Bar Chart";
import { BrowserRouter as Router, Switch, Route, Link } from 'react-router-dom';

class home extends Component {

  constructor(props){
    super(props)
    this.state = {
      data : [
        {
          "SEGMENTS": [
            {
              "HANDLE": "4660",
              "PARENT": "4664",
              "DISTANCE": 5,
              "CROSSPHASEADJUSTMENT": "10",
              "VARIANCE": 30,
              "DEVICES": [
                {
                  "ID": "ygqa3UlT46sdfDwac001",
                  "SIGNALSTRENGTHDISCREPANCY": "10",
                  "PRIMARY": []
                }
              ],
              "PATH": "1/4664"
            },
            
            {
              "HANDLE": "4664",
              "PARENT": "",
              "DISTANCE": 25,
              "CROSSPHASEADJUSTMENT": "30",
              "VARIANCE": 25,
              "DEVICES": [
                {
                  "ID": "ygqa3UlT46sdfDwac002",
                  "SIGNALSTRENGTHDISCREPANCY": "10",
                  "PRIMARY": []
                }
              ],
              "PATH": "1"
            },
            
            {
              "HANDLE": "4662",
              "PARENT": "4664",
              "DISTANCE": 15,
              "CROSSPHASEADJUSTMENT": "60",
              "VARIANCE": 40,
              "DEVICES": [
                {
                  "ID": "ygqa3UlT46sdfDwac007",
                  "SIGNALSTRENGTHDISCREPANCY": "10",
                  "PRIMARY": []
                }
              ],
              "PATH": "1/4664"
            }
          ],
          "UNASSIGNEDDEVICES": [
            {
              "ID": "vZyRYnQPaCimIeGCf4cdUcvnI2pYBRJr"
            }
          ],
          "LVGMFUNCTIONISDISABLED": []
        }
        
      ],
      count: 0
    }
  }

  count_devices(){
    var result = 0;

    for(let i = 0; i < this.state.data[0].SEGMENTS.length; i++) {
      result += this.state.data[0].SEGMENTS[i].DEVICES.length;
    }
    
    return result;
  }

  render() {    
    return (
      <Router>
          <div>
            <div align="left"> 
              <table>
                <tbody>
                  <tr>
                    <td><b><span> Transformer XXXXXXX</span></b></td>
                    <td><span> &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></td>
                    <td><span> &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></td>
                    <td><span> &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></td>
                    <td><span> &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></td>
                    <td><span> &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></td>
                    <td><span> &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></td>
                    <td><span> &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></td>
                    <td><span> &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></td>
                    <td><span> &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></td>
                    <td><span> &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></td>
                    <td><span> &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></td>
                    
                    <td>{this.state.data[0].SEGMENTS.length} Segments</td>
                    <td><span> &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></td>
                    <td>{this.count_devices()} Devices</td>
                    <td><span> &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></td>
                    <td><span> UTC+06:00 </span></td>
                  </tr>
                </tbody>
              </table>
            </div>
            <nav className="navbar navbar-expand-lg navbar-light bg-light">
            <ul className="navbar-nav mr-auto">
              <li><Link to={'/topology'} className="nav-link"> Topology </Link></li>
              <li><Link to={'/energybalance'} className="nav-link">Energy balance </Link></li>
              <li><Link to={'/voltage'} className="nav-link">Voltage </Link></li>
              <li>&nbsp;</li>
              <li>&nbsp;</li>
              <li>&nbsp;</li>
              <li>&nbsp;</li>
              <li>&nbsp;</li>
              <li>&nbsp;</li>
              <li>&nbsp;</li>
            </ul>
            </nav>
            <hr />
            <Switch>
                <Route exact path='/topology' component={RangeBarChart} />
            </Switch>
          </div>
        </Router>

      );
  }
}

export default home;
