	//#region Helper_Functions

	count_devices(){
		let count = 0;

		for(let i=0;i < this.state.segments.length;i++){
			count += this.state.segments[i]._devices.length;
		}
		return count;
	}

	toggleState() {
		this.setState({
			toggle: !this.state.toggle
		});
		console.log(this.state.toggle);
	}

	formatNumber (num) {
		return num.toString().replace(/(\d)(?=(\d{3})+(?!\d))/g, "$1,")
	}

	makeLabel(i,j){
		if(this.state.toggle)
			return this.state.power[i].PHASES[j].FORWARDACTIVEPOWER+this.state.power[i].PHASES[j].REVERSEACTIVEPOWER;
		else 
			return this.state.power[i].PHASES[j].RMSCURRENT;
	}

	makeleftLabel(i,j){
		return this.makeLabel(i-1,j)+ this.makeLabel(i+1,j);
	}

	makeParentLabel(i,j){
		return this.makeleftLabel(i-2,j)+this.makeleftLabel(i+2,j)+
			this.makeLabel(i-2,j)+this.makeLabel(i+2,j);
	}

	makeExamp(){
		console.log(this.state.segments);
		console.log(this.state.segments[1]);
		if(this.state.segments.length>1){
			console.log(this.state.segments[0]._handle);
			return this.state.segments[0]._handle;
		}
		
	}

	makeDataPoints(){
		let a = [];
		let intv = 15;
		for(let i=0;i<this.state.segments.length;i++){
			let mark = 0;
			if(this.state.segments[i]._parentHandle !== ""){
				if(i%2 === 0) {
					mark = intv*3;
					var powerBlock = {
						Label: this.state.segments[i]._power+"%", 
						color: "#E7E7E7",
						x: i,
						y: [mark+0.1,mark+1.75]
					}
					a.push(powerBlock);
				}
				else {
					mark = intv*2;
					powerBlock = {
						Label: this.state.segments[i]._power+"%", 
						color: "#E7E7E7",
						x: i,
						y: [mark+0.1,mark+1.75]
					}
					a.push(powerBlock);

					var childPower = {
						Label: 100-(parseInt(this.state.segments[i-1]._power)+parseInt(this.state.segments[i+1]._power))+"%",
						color: "#E7E7E7",
						x: i,
						y: [mark*(3/2)-1.75,mark*(3/2)]
					}
					a.push(childPower);
				}

				
			}
		}
		console.log(a);
		return a ;

	}

