	componentDidMount(){
		Axios.get(process.env.REACT_APP_SEGMENTS_API+"segments",
		{
			headers: {
				'Access-Control-Allow-Origin': '*',
				},
		},
		{
			withCredentials : true
		}
		).then(response=>{
			let a = response.data;
			this.setState({
				segments : a
			});
		});

		Axios.get(process.env.REACT_APP_POWER_API+"power",
		{
			headers: {
				'Access-Control-Allow-Origin': '*',
				},
		},
		{
			withCredentials : true
		}
		).then(response=>{
			//console.log(response.data);
			
		});
	}

