import React, { Component } from "react";
import Axios from "axios";
import Home from "./home";
import SignUp from "./signup.component";
import {Switch, Route, Link } from 'react-router-dom';

class Login extends Component {

    constructor(props){
        super(props);
        this.state = {
            showComponent: false,
            redirect : false,
            email: "",
            password: "",
            items: []
          };
          this._onButtonClick = this._onButtonClick.bind(this);
          this.handleEmailChange = this.handleEmailChange.bind(this);
          this.pwdChange = this.pwdChange.bind(this);
    }

    handleEmailChange(event) {
        this.setState({
          email: event.target.value,
        });
    }

    pwdChange(event) {
        this.setState({
            password: event.target.value,
        });
    }

    _onButtonClick(event) {

        console.log(this.state.email,this.state.password);
        console.log(process.env.NODE_ENV);
        Axios.post("http://localhost:8080/userapi/users/verify",
            {
                headers: {
                    'Access-Control-Allow-Origin': '*',
                  },
            },
            {
                data:{
                    firstname : "",
                    lastname : "",
                    email: this.state.email,
                    password : this.state.password
                }
            },
            {
                withCredentials : true
            }
        ).then(response=>{
            console.log(response.data.length);
            //if(response.data)
            if(response.data.length !== 0){
                this.props.handleSuccessfulAuth(response.data);
            }
        });
        event.preventDefault();

        //if(this.state.items.length !== 0){
            // this.setState({
            //     showComponent : true,
            // })
        //}
        console.log("props binary: "+this.props.binary)
    }



    render() {
        return (
            <div>
                { this.props.binary ? 
                    <div>
                        <Home/>
                    </div> :
                    (
                    <form>
                        <h3>Sign In</h3>

                        <div className="form-group">
                            <label>Email address</label>
                            <input type="email" className="form-control" placeholder="Enter email" onChange={this.handleEmailChange}/>
                        </div>

                        <div className="form-group">
                            <label>Password</label>
                            <input type="password" className="form-control" placeholder="Enter password" onChange={this.pwdChange} />
                        </div>

                        <div className="form-group">
                            <div className="custom-control custom-checkbox">
                                <input type="checkbox" className="custom-control-input" id="customCheck1" />
                                <label className="custom-control-label" htmlFor="customCheck1">Remember me</label>
                            </div>
                        </div>
                        <button type="submit" className="btn btn-primary btn-block" onClick={this._onButtonClick} >
                            Submit
                        </button>  

                        <p className="forgot-password text-right">
                            {/* <a href="/signup">Sign Up?</a> */}
                            <div align="right">
                                <Link to={'/signup'} className="nav-link">Sign Up? </Link>
                            </div>
                            <Switch>
                                <Route exact path='/signup' component={SignUp} />
                            </Switch>
                        </p>

                    </form>)
                }  
            </div>
        );
    }
}

export default Login;
