import React, { Component } from 'react';
import CanvasJSReact from '../../assets/canvasjs.react';
import "../Toggle.css";
import { Switch, Route, Link } from 'react-router-dom';
import Login from '../../login.component';
import home from '../../home';
import Axios from 'axios';
var CanvasJSChart = CanvasJSReact.CanvasJSChart;


//#region Style
const divStyle = {
	height: '550px',
	width : '950px',
	backgroundColor: '#FFFFFF',
	resize: 'horizontal',
	overflow: 'auto'
};

const divStyle2 = {
	height: '550px',
	width: '400px',
	backgroundColor: '#E7E7E7',
	resize: 'horizontal',
	overflow: 'auto'
};

const parentdivStyle = {
	backgroundColor : '#E6E6E6'
}  
//#endregion

class VoltageChart extends Component {
	constructor(props){
		super(props)
		this.state = {
			data : [],
			power : [],
			currentIndex: 0,
			path: "",
			SegNo: "-",
			distance: 0,
			dtt : "",
			variance : 0,
			cpa : 0,
			devices : [],
			clr : ["#FCC984","#FCC984","#FCC984","#FCC984","#FCC984","#FCC984","#FCC984"],
			toggle : true
		};
		this.toggleState = this.toggleState.bind(this);
		this.makeDataPoints = this.makeDataPoints.bind(this);
	}

	
	toggleState() {
		this.setState({
			toggle: !this.state.toggle
		});
		console.log(this.state.toggle);
	}

	formatNumber (num) {
		return num.toString().replace(/(\d)(?=(\d{3})+(?!\d))/g, "$1,")
	}

	makeLabel(i,j){
		return this.state.power[i]._phases[j]._rmsVoltage;
	}

	makeleftLabel(i,j){
		return this.makeLabel(i-1,j)+ this.makeLabel(i+1,j);
	}

	makeParentLabel(i,j){
		return this.makeleftLabel(i-2,j)+this.makeleftLabel(i+2,j)+
			this.makeLabel(i-2,j)+this.makeLabel(i+2,j);
	}

	makeDataPoints(){
		let a = [];
		let intv = 15;
		for(let i=0;i<this.state.data.length;i++){
			let mark = 0;
			if(this.state.segments[i]._parentHandle !== ""){
				if(i%2 === 0) {
					mark = intv*3;

					var powerBlock = {
						Label: this.state.data[i]._power+"%", 
						color: "#E7E7E7",
						x: i,
						y: [mark+0.1,mark+1.75]
					}
					var segmentBlock ={ 
						Label: this.state.data[i]._handle,
						color: this.state.clr[i],
						x: i,
						y: [mark+1.75,mark+4.25],
						mouseover: function(e){
							this.setState(
								{
									currentIndex : i
								}
							);
						}.bind(this),
						
					}
					var p1 ={ 
						Label: this.makeLabel(i,0),
						color: "#FFFFFF",
						x: i + 0.2,
						y: [mark+4.75,mark+6.25],
						indexLabel: "",
						indexLabelFontSize: 11,
						indexLabelFontColor : "#6D2A00"
					}
					var p2 ={ 
						Label: this.makeLabel(i,1),
						color: "#FFFFFF",
						x: i,
						y: [mark+4.75,mark+6.25],
						indexLabel: "",
						indexLabelFontSize: 11,
						indexLabelFontColor : "#2C2C2D"
					}
					var p3 ={ 
						Label: this.makeLabel(i,2),
						color: "#FFFFFF",
						x: i - 0.2,
						y: [mark+4.75,mark+6.25],
						indexLabel: "",
						indexLabelFontSize: 11,
						indexLabelFontColor : "#B1B1B9"
					}
					a.push(powerBlock,segmentBlock,p1,p2,p3);

				}
				else {
					mark = intv*2;

					powerBlock = {
						Label: this.state.data[i]._power+"%", 
						color: "#E7E7E7",
						x: i,
						y: [mark+0.1,mark+1.75]
					}
					var childPower = {
						Label: 100-(parseInt(this.state.data[i-1]._power)+parseInt(this.state.data[i+1]._power))+"%",
						color: "#E7E7E7",
						x: i,
						y: [mark*(3/2)-1.75,mark*(3/2)]
					}
					segmentBlock ={ 
						Label: this.state.data[i]._handle,
						color: this.state.clr[i],
						x: i,
						y: [mark+1.75,mark+4.25],
						mouseover: function(e){
							this.setState(
								{
									currentIndex : i
								}
							);
						}.bind(this),
						
					}
					p1 = { 
						Label: this.makeLabel(i,0),
						color: "#FFFFFF",
						x: i + 0.2,
						y: [mark+4.75,mark+6.25],
						indexLabel: "",
						indexLabelFontSize: 11,
						indexLabelFontColor : "#6D2A00"
					}
					p2 = { 
						Label: this.makeLabel(i,1),
						color: "#FFFFFF",
						x: i,
						y: [mark+4.75,mark+6.25],
						indexLabel: "",
						indexLabelFontSize: 11,
						indexLabelFontColor : "#2C2C2D"
					}
					p3 = { 
						Label: this.makeLabel(i,2),
						color: "#FFFFFF",
						x: i - 0.2,
						y: [mark+4.75,mark+6.25],
						indexLabel: "",
						indexLabelFontSize: 11,
						indexLabelFontColor : "#B1B1B9"
					}
					var absorbed_p1 = { 
						Label: this.makeleftLabel(i,0),
						color: "#FFFFFF",
						x: i+0.2,
						y: [mark+11.25,mark+12.75],
						indexLabel: "",
						indexLabelFontSize: 11,
						indexLabelFontColor : "#6D2A00"
					}
					var absorbed_p2 = { 
						Label: this.makeleftLabel(i,1),
						color: "#FFFFFF",
						x: i,
						y: [mark+11.25,mark+12.75],
						indexLabel: "",
						indexLabelFontSize: 11,
						indexLabelFontColor : "#2C2C2D"
					}
					var absorbed_p3 = { 
						Label: this.makeleftLabel(i,2),
						color: "#FFFFFF",
						x: i-0.2,
						y: [mark+11.25,mark+12.75],
