	componentDidMount(){
		Axios.get(process.env.REACT_APP_SEGMENTS_API+"segments",
		{
			headers: {
				'Access-Control-Allow-Origin': '*',
				},
		},
		{
			withCredentials : true
		}
		).then(response=>{
			this.setState({
				data : response.data
			});
		});

		Axios.get(process.env.REACT_APP_POWER_API+"power",
		{
			headers: {
				'Access-Control-Allow-Origin': '*',
				},
		},
		{
			withCredentials : true
		}
		).then(response=>{
			this.setState({
				power : response.data
			})
		});
	}

