import React,{ Component} from 'react';
import DataTable from 'react-data-table-component';
import orderBy from 'lodash/orderBy';

const data = [{ title: '12 Angry Men', director: 'Sidney Lumet', year: '1957' },
                { title: 'A Separation', director: 'Asghar Farhadi', year: '2011' },
                { title: 'Alice in Wonderland', director: 'Tim Burton', year: '2010' },
                { title: 'Alive', director: 'Frank Marshall', year: '1993' },
                { title: 'All Quiet on the Western Front', director: 'Lewis Milestone', year: '1930' }, 
                { title: 'Before Sunrise', director: 'Richard Linklater', year: '1995' }, 
                { title: 'Chinatown', director: 'Roman Polanski', year: '1974' }, 
                { title: 'Das Boot', director: 'Wolfgang Petersen', year: '1981' }, 
                { title: 'I-See-You.Com', director: 'Eric Steven Stahl', year: '2006' }
            ];
const columns = [
    {
      name: 'Attribute',
      selector: 'title',
      sortable: true,
    },
    {
      name: 'Director',
      selector: 'director',
      sortable: true,
    },
    {
      name: 'Year',
      selector: 'year',
      sortable: true,
    },
  ];
 
const handleSort = (column, sortDirection) => console.log(column.selector, sortDirection);

const customSort = (rows, field, direction) => {
    const handleField = row => {
      if (row[field]) {
        return row[field].toLowerCase();
      }
  
      return row[field];
    };
  
    return orderBy(rows, handleField, direction);
  };

class LPDT extends Component{
    
    render() {
        return (
          <DataTable
            title="Load Profile Data"
            columns={columns}
            data={data}
            fixedHeader
            fixedHeaderScrollHeight="600px"
            pagination
            onSort={handleSort}
            sortFunction={customSort}
          />
        )
      }
};

export default LPDT;
