const divStyle = {
	height: '550px',
	width : '900px',
	backgroundColor: '#FFFFFF',
	resize: 'horizontal',
	overflow: 'auto'
};

const divStyle2 = {
	height: '550px',
	width: '500px',
	backgroundColor: '#383837',
	resize: 'horizontal',
	overflow: 'auto'
};

const parentdivStyle = {
	backgroundColor : '#E6E6E6'
}  

class RangeBarChart extends Component {

		constructor(props){
			super(props)
			this.state = {
				data : [
					{
						"SEGMENTS": [
							{
								"HANDLE": "4672",
								"PARENT": "4664",
								"DISTANCE": 10,
