									{
										"ID": "ygqa3UlT46sdfDwac001",
										"SIGNALSTRENGTHDISCREPANCY": "10",
										"PRIMARY": []
									}
								],
								"PATH": "1/4664"
							},

							{
								"HANDLE": "4670",
								"PARENT": "4664",
								"DISTANCE": 15,
								"CROSSPHASEADJUSTMENT": "10",
								"VARIANCE": 45,
								"DEVICES": [
									{
										"ID": "ygqa3UlT46sdfDwac691",
										"SIGNALSTRENGTHDISCREPANCY": "10",
										"PRIMARY": []
									}
								],
								"PATH": "1/4664/4660"
							},
							
							{
								"HANDLE": "4664",
								"PARENT": "",
								"DISTANCE": 25,
								"CROSSPHASEADJUSTMENT": "30",
								"VARIANCE": 25,
								"DEVICES": [
									{
										"ID": "ygqa3UlT46sdfDwac002",
										"SIGNALSTRENGTHDISCREPANCY": "10",
										"PRIMARY": []
									}
								],
								"PATH": "1"
							},

							{
								"HANDLE": "4668",
								"PARENT": "4662",
								"DISTANCE": 15,
								"CROSSPHASEADJUSTMENT": "10",
								"VARIANCE": 55,
								"DEVICES": [
									{
										"ID": "ygqa3UlT46sdfDwac129",
										"SIGNALSTRENGTHDISCREPANCY": "10",
										"PRIMARY": []
									}
								],
								"PATH": "1/4664/4662"
							},
							
							{
								"HANDLE": "4662",
								"PARENT": "4664",
								"DISTANCE": 15,
								"CROSSPHASEADJUSTMENT": "60",
								"VARIANCE": 40,
								"DEVICES": [
									{
										"ID": "ygqa3UlT46sdfDwac007",
										"SIGNALSTRENGTHDISCREPANCY": "10",
										"PRIMARY": []
									}
								],
								"PATH": "1/4664"
							},

							{
								"HANDLE": "4666",
								"PARENT": "4662",
								"DISTANCE": 10,
								"CROSSPHASEADJUSTMENT": "10",
								"VARIANCE": 50,
								"DEVICES": [
									{
										"ID": "ygqa3UlT46sdfDwac009",
										"SIGNALSTRENGTHDISCREPANCY": "10",
										"PRIMARY": []
									}
								],
								"PATH": "1/4664/4662"
							},
						],
						"UNASSIGNEDDEVICES": [
							{
								"ID": "vZyRYnQPaCimIeGCf4cdUcvnI2pYBRJr"
