import React, { Component } from "react";
import Home from "./home";

class Login extends Component {

    constructor(props){
        super(props);
        this.state = {
            showComponent: false,
            redirect : false,
            email: "a",
            password: "a",
            items: []
          };
          this._onButtonClick = this._onButtonClick.bind(this);
          this.handleEmailChange = this.handleEmailChange.bind(this);
          this.pwdChange = this.pwdChange.bind(this);
    }

    handleEmailChange(event) {
        this.setState({
          email: event.target.value,
        });
    }

    pwdChange(event) {
        this.setState({
            password: event.target.value,
        });
    }

    _onButtonClick() {
        eslint-disable-next-line
        var res = fetch("http://localhost:8085/users/verify",{
            method: 'POST',
            body : JSON.stringify({
                "firstName" : "Saiful",
                "lastName" : "Islam",
                "email" : this.state.email,
                "password" : this.state.password
            }),
            headers:{
                'Content-Type' : 'application/json'
            }
        }).then(res => res.json())
        .then(
            (result) => {
                console.log(result);
              this.setState({

                items: result
              });
            },
            (error) => {
              console.log("Error : "+ error)
            }
          )


        if(this.state.items.length !== 0){
            this.setState({
                showComponent : true,
            })
        }
        console.log(this.state.items)
    }



    render() {
        return (
            <div>
                {this.state.showComponent ? 
                    <div>
                        <Home/>
                    </div> :(
                    <form>
                        <h3>Sign In</h3>

                        <div className="form-group">
                            <label>Email address</label>
                            <input type="email" className="form-control" placeholder="Enter email" onChange={this.handleEmailChange}/>
                        </div>

                        <div className="form-group">
                            <label>Password</label>
                            <input type="password" className="form-control" placeholder="Enter password" onChange={this.pwdChange} />
                        </div>

                        <div className="form-group">
                            <div className="custom-control custom-checkbox">
                                <input type="checkbox" className="custom-control-input" id="customCheck1" />
                                <label className="custom-control-label" htmlFor="customCheck1">Remember me</label>
                            </div>
                        </div>
                        <button type="submit" className="btn btn-primary btn-block" onClick={this._onButtonClick} >
                            Submit
                        </button>  

                        <p className="forgot-password text-right">
                            <a href="/">Forgot password?</a>
                        </p>

                    </form>)
                }  
            </div>
        );
    }
}

export default Login;
