import React,{ Component} from 'react';
import DataTable from 'react-data-table-component';
import orderBy from 'lodash/orderBy';
import Dropdown from 'react-dropdown';
import 'react-dropdown/style.css';
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import Axios from 'axios';
import Select from 'react-select';

const data = [{ title: '12 Angry Men', director: 'Sidney Lumet', year: '1957' },
                { title: 'A Separation', director: 'Asghar Farhadi', year: '2011' },
                { title: 'Alice in Wonderland', director: 'Tim Burton', year: '2010' },
                { title: 'Alive', director: 'Frank Marshall', year: '1993' },
                { title: 'All Quiet on the Western Front', director: 'Lewis Milestone', year: '1930' }, 
                { title: 'Before Sunrise', director: 'Richard Linklater', year: '1995' }, 
                { title: 'Chinatown', director: 'Roman Polanski', year: '1974' }, 
                { title: 'Das Boot', director: 'Wolfgang Petersen', year: '1981' }, 
                { title: 'I-See-You.Com', director: 'Eric Steven Stahl', year: '2006' }
            ];
const columns = [
    {
      name: 'Attribute',
      selector: 'title',
      sortable: true,
    },
    {
      name: 'Director',
      selector: 'director',
      sortable: true,
    },
    {
      name: 'Year',
      selector: 'year',
      sortable: true,
    },
  ];
 
const handleSort = (column, sortDirection) => console.log(column.selector, sortDirection);

const customSort = (rows, field, direction) => {
    const handleField = row => {
      if (row[field]) {
        return row[field].toLowerCase();
      }
  
      return row[field];
    };
  
    return orderBy(rows, handleField, direction);
  };

const options = [
    'one', 'two', 'three'
]

const hours =[
  '01:00:00','02:00:00','03:00:00','04:00:00','05:00:00','06:00:00','07:00:00','08:00:00'
  ,'09:00:00','10:00:00','11:00:00','12:00:00','13:00:00','14:00:00','15:00:00','16:00:00'
  ,'17:00:00','18:00:00','19:00:00','20:00:00','21:00:00','22:00:00','23:00:00','24:00:00'
]


class LPDT extends Component{

    constructor(props){
      super(props);
      this.state = {
        startDate: new Date(),
        devices :[],
        id : "",
        sdate : "",
        shour : "",
        ehour : ""
      };
      this._onSelect = this._onSelect.bind(this);
      this._onhourSelect = this._onhourSelect.bind(this);
      this._onEndHourSelect = this._onEndHourSelect.bind(this);
      this.handleChange = this.handleChange.bind(this);
    }

    

  getParsedDate(date){
    date = String(date).split(' ');
    var year = String(date[3]).split('-');
    var month = new Date(Date.parse(String(date[1]).split(':') +" 1, 2012")).getMonth()+1;
    if(month< 10) month = "0"+month;
    var day = String(date[2]).split(':');
    return year+"-"+month+"-"+day+" ";
  }

    _onSelect(event){
      console.log(event);
      this.setState({
        id : event.label
      })
    }

    _onhourSelect(event){
      this.setState({
        shour : event.label,
      });
    }

    _onEndHourSelect(event){
      this.setState({
        ehour : event.label,
      });
    }

    handleChange = date => {
      var s = this.getParsedDate(date);
      console.log(s);
      this.setState({
        sdate : s,
      });
      console.log(this.state.sdate );
    };


    componentDidMount() {
      Axios.get("http://localhost:8090/getDevices",
      {
          headers: {
              'Access-Control-Allow-Origin': '*',
            },
      },
      {
          withCredentials : true
      }
      ).then(response=>{
          console.log(response.data);
          this.setState({
            devices : response.data
          })
      });
    }
    
    render() {
        return (
          <div>
            <div>
              <table>
                <tbody>
                  <tr>
                    <td><label>Device ID:  </label></td>
                    <td><span> &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></td>
                    <td><Dropdown options={this.state.devices} onChange={this._onSelect} placeholder="Select an option" /></td> 
                    <td>
                    <Select
                      onChange={this._onSelect}
                      options={this.state.devices}
                    />
                    </td>
                    <td><span> &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></td>
                    <td><label>Select Date:  </label></td>
                    <td><span> &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></td>
                    <td><DatePicker selected={this.state.startDate} onChange={this.handleChange}/></td>
                  </tr>
                  <tr>
                    <td><label>From:  </label></td>
                    <td><span> &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></td>
                    <td><Dropdown options={hours} onChange={this._onhourSelect} placeholder="Select an option" /></td>
                    <td><span> &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></td>
                    <td><label>  To:  </label></td>
                    <td><span> &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></td>
                    <td><Dropdown options={hours} onChange={this._onEndHourSelect} placeholder="Select an option" /></td>
                  </tr>
                </tbody> 
              </table>
            </div>

            <div align="left">
                <DataTable
                  title="Load Profile Data"
                  columns={columns}
                  data={data}
                  fixedHeader
                  fixedHeaderScrollHeight="400px"
                  pagination
                  onSort={handleSort}
                  sortFunction={customSort}
              />
            </div>
          </div>
        )
      }
};

export default LPDT;
