import React,{ Component} from 'react';
import 'react-dropdown/style.css';
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import Axios from 'axios';
import Select from 'react-select';
import Table from 'react-bootstrap/Table'
import "../Toggle.css";
import { Switch, Route, Link } from 'react-router-dom';
import home from '../../home';
import Login from '../../login.component';


const ex1 = {
  overflow: 'scroll',
  height : '550px'
}

//#region Constants
const hours =[
  {value : '01:00:00', label: '01:00:00'},{value : '02:00:00', label: '02:00:00'},
  {value : '03:00:00', label: '03:00:00'},{value : '04:00:00', label: '04:00:00'},
  {value : '05:00:00', label: '05:00:00'},{value : '06:00:00', label: '06:00:00'},
  {value : '07:00:00', label: '07:00:00'},{value : '08:00:00', label: '08:00:00'},
  {value : '09:00:00', label: '09:00:00'},{value : '10:00:00', label: '10:00:00'},
  {value : '11:00:00', label: '11:00:00'},{value : '12:00:00', label: '12:00:00'},
  {value : '13:00:00', label: '13:00:00'},{value : '14:00:00', label: '14:00:00'},
  {value : '15:00:00', label: '15:00:00'},{value : '16:00:00', label: '16:00:00'},
  {value : '17:00:00', label: '17:00:00'},{value : '18:00:00', label: '18:00:00'},
  {value : '19:00:00', label: '19:00:00'},{value : '20:00:00', label: '20:00:00'},
  {value : '21:00:00', label: '21:00:00'},{value : '22:00:00', label: '22:00:00'},
  {value : '23:00:00', label: '23:00:00'},{value : '24:00:00', label: '24:00:00'},
]
//#endregion

class LPDT extends Component{

    constructor(props){
      super(props);
      this.state = {
        startDate: new Date(),
        devices :[],
        id : "",
        sdate : "",
        shour : "",
        ehour : "",
        profile : [],
        allprofile : [],
        index : 0,
        toggle : true
      };
      this._onDeviceSelect = this._onDeviceSelect.bind(this);
      this._onhourSelect = this._onhourSelect.bind(this);
      this._onEndHourSelect = this._onEndHourSelect.bind(this);
      this.handleDateChange = this.handleDateChange.bind(this);
      this._onButtonClick = this._onButtonClick.bind(this);
      this.getColumnTitle = this.getColumnTitle.bind(this);
      this.getColumnName = this.getColumnName.bind(this);
      this.toggleState = this.toggleState.bind(this);
      this.getFirstDiffVal = this.getFirstDiffVal.bind(this);
      this.processResponse = this.processResponse.bind(this);
      this.getAttributeName = this.getAttributeName.bind(this);
      this.getRowHeader = this.getRowHeader.bind(this);
    }

    //#region Helper

    getParsedDate(date){
      date = String(date).split(' ');
      var year = String(date[3]).split('-');
      var month = new Date(Date.parse(String(date[1]).split(':') +" 1, 2012")).getMonth()+1;
      if(month< 10) month = "0"+month;
      var day = String(date[2]).split(':');
      return year+"-"+month+"-"+day+" ";
    }

    getColumnTitle(pk){
      var date = pk._dateTime;
      date = String(date).split(' ');
      var time = String(date[1]).split(':');

      return time[0]+":"+ time[1];
    }

    getColumnName(){
      if(this.state.profile.length !== 0){
        return "Forward Active energy Wh L1+L2+L3";
      }
      return " ";
    }

    getFirstDiffVal(idx){
      if(this.state.allprofile.length !== 0){
        return this.state.allprofile[idx]._channelValue;
      }

      else if(this.state.profile.length !== 0){
        if(!this.state.toggle && this.state.shour.includes("01:00")){
          return "N/A";
        }
        else return this.state.profile[0]._channelValue;
      }
      else return "";
    }

    getAttributeName(){
      if(this.state.allprofile.length > 1){
        return "Forward Active energy Wh L1+L2+L3";
      }
      return "Attribute";
    }

    getRowHeader(arr){
      if(this.state.allprofile.length > 1){
        const _device = this.state.devices.find(device => device.value === arr[0]._pK._deviceId);
        return _device.label;
      }
      else return "Forward Active energy Wh L1+L2+L3";
    }


    processResponse(response){
      //console.log("Inside process response");
      let dArr = [];
      for(let i=0;i<this.state.devices.length-1;i++){
        let arr = [];
        for(let j=0;j<response.length;j++){
          if(response[j]._pK._deviceId === this.state.devices[i].value){
            //console.log("Matched "+this.state.devices[i].value + "\n");
            arr.push(response[j]);
          }
        }
        if(arr.length !==0){
          dArr.push(arr);
          //if(this.state.profile.length === 0){
            this.setState({
              profile : dArr[0]
            })
          //}
        }
        console.log(arr.length);
      }
      console.log(dArr.length);
      this.setState({
        allprofile : dArr,
      })
    }

    //#endregion

    //#region Events


    _onDeviceSelect(event){
      this.setState({
        id : event.value
      })
    }

    _onhourSelect(event){
      this.setState({
        shour : event.label,
      });
    }

    _onEndHourSelect(event){
      this.setState({
        ehour : event.label,
      });
    }

    handleDateChange = date => {
      var s = this.getParsedDate(date);
      this.setState({
        startDate : date,
        sdate : s
      });
    };

    toggleState() {
      this.setState({
        toggle: !this.state.toggle
      });
      console.log(this.state.toggle);
    }

    _onButtonClick(event) {
      var api ;

      if(this.state.id !== "All"){
        if(this.state.toggle) api =process.env.REACT_APP_LPD_API+"getProfile";
        else api = process.env.REACT_APP_LPD_API +"getProfileDiff";
      }
      else {
        if(this.state.toggle) api = process.env.REACT_APP_LPD_API + "getAllProfile"
        else api = process.env.REACT_APP_LPD_API +"getAllProfileDiff";
      };

      Axios.post(api,
      {
          headers: {
              'Access-Control-Allow-Origin': '*',
            },
      },
      {
        data:{
            _id:this.state.id,
            _startDate: this.state.sdate+this.state.shour+".000",
            _endDate: this.state.sdate+this.state.ehour+".000"
        }
      },
      {
          withCredentials : true
      }
      ).then(response=>{
          console.log("response : " + response.data.length);
          this.processResponse(response.data);
      });

    }


    componentDidMount() {
      Axios.get(process.env.REACT_APP_LPD_API+"getDevices",
      {
          headers: {
              'Access-Control-Allow-Origin': '*',
            },
      },
      {
          withCredentials : true
      }
      ).then(response=>{
          console.log(response.data);
          this.setState({
            devices : response.data
          })
      });
    }
    //#endregion

    render() {
        return (
          <div style={ex1}>
            <div align="left">
              <table>
                <tbody>
                  <tr>
                    <td><span> &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></td>
                    <td><span> &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></td>
                    <td><span> &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></td>
                    <td><span> &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></td>
                    <td><span> &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></td>
                    <td><span> &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></td>
                    <td><span> &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></td>
                    <td><span> &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></td>
                    <td><span> &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></td>
                    <td><span> &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></td>
                    <td><span> &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></td>
                    <td><span> &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></td>
                    <td><span> &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></td>
                    <td><span> &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></td>
                    <td><span> &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></td>
                    <td><span> &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></td>
                    <td><span> &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></td>
                    <td><span> &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></td>
                    <td><span> &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></td>
                    <td>
                      <Link to={'/navigation'}>Log Out</Link>
                      <Switch>
                        <Route exact path='/navigation' component={Login}/>
                      </Switch>
                    </td>
                  </tr>
                </tbody>
              </table>
            </div>

            <div>
            <nav className="navbar navbar-expand-lg navbar-light bg-light">
              <ul className="navbar-nav mr-auto">
                  <li><Link to={'/home'} className="nav-link">Home </Link></li>
                  <li>&nbsp;</li>
                  <li>&nbsp;</li>
                  <li>&nbsp;</li>
                  <li>&nbsp;</li>
                  <li>&nbsp;</li>
                  <li>&nbsp;</li>
              </ul>
            </nav>
            <Switch>
                <Route exact path ='/home' component={home}/>
            </Switch>
            </div>

            <div>
              <table>
                <tbody>
                  <tr>
                    <td><label>Device ID:  </label></td>
                    <td><span> &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></td>
                    <td>
                      <Select
                        onChange={this._onDeviceSelect}
                        options={this.state.devices}
                        placeholder = "Select Device"
                        value={this.state.devices.filter(option => option.label === 'All Devices')}
                      />
                    </td>
                  </tr>
                  <tr>
                  <td><label>Select Date:  </label></td>
                    <td><span> &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></td>
                    <td><DatePicker selected={this.state.startDate} onChange={this.handleDateChange}/></td>
                  </tr>
                  <tr>
                    <td><label>From:  </label></td>
                    <td><span> &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></td>
                    <td>
                      <Select
                        onChange={this._onhourSelect}
                        options={hours}
                        placeholder = "Select Time"
                      />
                    </td>
                  </tr>
                  <tr>
                    <td><label> To:  </label></td>
                    <td><span> &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></td>
                    <td>
                      <Select
                        onChange={this._onEndHourSelect}
                        options={hours}
                        placeholder = "Select Time"
                      />
                    </td>
                  </tr>
                  <tr>
                    <td>
                      <form className="switch-field">
                        <div className="switch-title">{""}</div>
                        <input type="radio" id="switch_left" name="switchToggle"
                          value={"Diff"} onChange={this.toggleState}
                          checked={!this.state.toggle}/>
                        <label htmlFor="switch_left">{"Diff"}</label>

                        <input type="radio" id="switch_right" name="switchToggle"
                          value={"Value"} onChange={this.toggleState}
                          checked={this.state.toggle}/>
                        <label htmlFor="switch_right">{"Value"}</label>
                      </form>
                    </td>
                  </tr>
                  <tr>
                    <td>
                      <button type="submit" onClick={this._onButtonClick} >
                            Submit
                        </button>
                    </td>
                  </tr>
                </tbody>
              </table>
            </div>

            <div>
                <div>
                  <div align="left">
                      <h3 align="left">Load Profile Data</h3>
                      <Table align="left" responsive="xl">
                        <thead align="left">
                          <tr>
                            <th align="left">{this.getAttributeName()}</th>
                            {this.state.profile.map(event => (
                              <th align="left">
                                {this.getColumnTitle(event._pK)}</th>
                            ))}
                          </tr>
                        </thead>
                        <tbody>
                          {
                            this.state.allprofile.map(subArr =>
                              <tr>
                                <td align="left">{this.getRowHeader(subArr)}</td>
                                {subArr.slice(0,subArr.length).map(event => (
                                  <td align="left">{event._channelValue}</td>

                                ))}
                              </tr>
                            )
                          }
                        </tbody>
                      </Table>
                  </div>
                </div>
            </div>
          </div>
        )
      }
};

export default LPDT;
