import React, { Component } from 'react';
import CanvasJSReact from '../../assets/canvasjs.react';
var CanvasJSChart = CanvasJSReact.CanvasJSChart;

// var bgColors = { "Default": "#81b71a",
//                     "Blue": "#00B1E1",
//                     "Cyan": "#37BC9B",
//                     "Green": "#8CC152",
//                     "Red": "#E9573F",
// 					"Yellow": "#F6BB42",
// 					"White" : "#FFFFFF",
// 					"Black" : "#000000",
// };

const divStyle = {
	height: '550px',
	width : '900px',
	backgroundColor: 'lightgray',
	resize: 'horizontal',
	overflow: 'auto'
  };

const divStyle2 = {
	height: '550px',
	width: '500px',
	backgroundColor: 'black',
	resize: 'horizontal',
	overflow: 'auto'
  };
 
class RangeBarChart extends Component {

		constructor(props){
			super(props)
			this.state = {
				data : [
					{
						"SEGMENTS": [
							{
								"HANDLE": "4660",
								"PARENT": "4664",
								"DISTANCE": 5,
								"CROSSPHASEADJUSTMENT": "0",
								"VARIANCE": 30,
								"DEVICES": [
									{
										"ID": "ygqa3UlT46sdfDwacw79cBr49M9j1iw9",
										"SIGNALSTRENGTHDISCREPANCY": "10",
										"PRIMARY": []
									}
								],
								"PATH": "1/4664"
							},
							
							{
								"HANDLE": "4664",
								"PARENT": "",
								"DISTANCE": 25,
								"CROSSPHASEADJUSTMENT": "0",
								"VARIANCE": 25,
								"DEVICES": [
									{
										"ID": "ygqa3UlT46sdfDwacw79cBr49M9j1iw0",
										"SIGNALSTRENGTHDISCREPANCY": "10",
										"PRIMARY": []
									}
								],
								"PATH": "1"
							},
							
							{
								"HANDLE": "4662",
								"PARENT": "4664",
								"DISTANCE": 15,
								"CROSSPHASEADJUSTMENT": "0",
								"VARIANCE": 40,
								"DEVICES": [
									{
										"ID": "ygqa3UlT46sdfDwacw79cBr49M9j1iw8",
										"SIGNALSTRENGTHDISCREPANCY": "10",
										"PRIMARY": []
									}
								],
								"PATH": "1/4664"
							}
						],
						"UNASSIGNEDDEVICES": [
							{
								"ID": "vZyRYnQPaCimIeGCf4cdUcvnI2pYBRJr"
							}
						],
						"LVGMFUNCTIONISDISABLED": []
					}
					
				],
				currentIndex: -1,
				path: "",
				SegNo: "-",
				distance: 0,
				dtt : "",
				variance : 0,
				cpa : 0,
				devices : []
			}
		}

		
		componentDidMount() {
			//console.log(this.state.data[0].SEGMENTS[0].HANDLE);
	  	}
		render() {
		const options = {
			animationEnabled: true,
			dataPointMaxWidth: 12,
			title:{
				text: "",
				fontFamily: "helvetica"
			},
			subtitles: [{
				text: "",
				fontFamily: "helvetica"
			}],
			axisY: {
				title: "",
				prefix: "",
				lineThickness: 1
			},
			axisX: {
				title: "",
				prefix: "",
				lineThickness: 0,
				interval : 1,
				tickLength : 0,
				labelPlacement : "inside",
				margin : 0,
				valueFormatString : " "
			},
		
			data: [{
				type: "rangeBar",
				yValueFormatString: "#,##0",
				click: function(e){
					this.setState(
						{
							path : this.state.data[0].SEGMENTS[this.state.currentIndex].PATH,
							SegNo: this.state.data[0].SEGMENTS[this.state.currentIndex].HANDLE,
							distance: this.state.data[0].SEGMENTS[this.state.currentIndex].DISTANCE,
							dtt : this.state.data[0].SEGMENTS[this.state.currentIndex].VARIANCE,
							variance : this.state.data[0].SEGMENTS[this.state.currentIndex].VARIANCE,
							cpa : this.state.data[0].SEGMENTS[this.state.currentIndex].CROSSPHASEADJUSTMENT,
							devices: this.state.data[0].SEGMENTS[this.state.currentIndex].DEVICES
						}
					);
				}.bind(this),
				dataPoints: [
					{ 
						label: this.state.data[0].SEGMENTS[0].HANDLE,
						y: [this.state.data[0].SEGMENTS[0].VARIANCE-this.state.data[0].SEGMENTS[0].DISTANCE, 
						this.state.data[0].SEGMENTS[0].VARIANCE],
						mouseover: function(e){
							this.setState(
								{
									currentIndex : 0
								}
							);
						}.bind(this),
						
					},
					{ 
						label: this.state.data[0].SEGMENTS[1].HANDLE, 
						y: [this.state.data[0].SEGMENTS[1].VARIANCE-this.state.data[0].SEGMENTS[1].DISTANCE, 
						this.state.data[0].SEGMENTS[1].VARIANCE] ,
						mouseover: function(e){
							this.setState(
								{
									currentIndex : 1
								}
							);
						}.bind(this)
					},
					{ 
						label: this.state.data[0].SEGMENTS[2].HANDLE, 
						y: [this.state.data[0].SEGMENTS[2].VARIANCE-this.state.data[0].SEGMENTS[2].DISTANCE, 
						this.state.data[0].SEGMENTS[2].VARIANCE],
						mouseover: function(e){
							this.setState(
								{
									currentIndex : 2
								}
							);
						}.bind(this)
					}
				
				]
			}]
		}
		
		return (
		<div>
			<table>
				<tbody>
					<tr>
						<td>
							<div style={divStyle}>
								{/* <div><span>Transformer</span></div> */}
								<div><span>&nbsp;&nbsp;&nbsp;&nbsp;</span></div>
								<CanvasJSChart options = {options} 
								/>
							</div>
						</td>

						<td>
							<div id="wrapper" style={divStyle2}>
								<font color={'White'}>
									<p>{this.state.path}</p>
									<h3><span>{this.state.SegNo}</span></h3>
									<div><span>Distance : {this.state.Distance}</span></div>
									<div><span>Distance to transformer : {this.state.dtt}</span></div>
									<div><span>Variance : {this.state.variance}</span></div>
									<div><span>Cross phase Adjustment : {this.state.cpa}</span></div>
									<div><span>&nbsp;</span> </div>
									<div><span>Devices :</span></div>

									<div>
										{/* <div><span>&nbsp;</span> </div> */}
										<div>
											<span>
												&nbsp;HP&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Serial No.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;
												&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;GHI score
											</span>
											<br/>
											{
												this.state.devices.map(function(d, idx){
													return (<span>
														&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; {d.ID} &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;
														&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;
														</span>)
												})	
											}
										</div>
										<div><span>&nbsp;</span> </div>
									</div>
								</font>
							</div>
						</td>
					</tr>
				</tbody>
			</table>
		</div>
		);
	}
}

export default RangeBarChart;
