class LPDT extends Component{

    constructor(props){
      super(props);
      this.state = {
        startDate: new Date(),
        devices :[],
        id : "",
        sdate : "",
        shour : "",
        ehour : "",
        profile : []
      };
      this._onDeviceSelect = this._onDeviceSelect.bind(this);
      this._onhourSelect = this._onhourSelect.bind(this);
      this._onEndHourSelect = this._onEndHourSelect.bind(this);
      this.handleDateChange = this.handleDateChange.bind(this);
      this._onButtonClick = this._onButtonClick.bind(this);
      this.processResponse = this.processResponse.bind(this);
      this.processColumn = this.processColumn.bind(this);
    }

    processColumn(){
      let col= Array(this.state.profile.length+1);
      col.push({name: 'Attribute',selector: 'title',sortable: true,});
      
      for(let i=0; i< this.state.profile.length;i++){
        var s = this.state.profile[i]._pK._dateTime;
        console.log(s);
      }

      console.log("Leaving processColumn");
    }

    processResponse(response){
      this.processColumn();
      console.log(response[0]);
      columns[1].omit = true;
    }
    
    //#region Events 
    getParsedDate(date){
      date = String(date).split(' ');
      var year = String(date[3]).split('-');
      var month = new Date(Date.parse(String(date[1]).split(':') +" 1, 2012")).getMonth()+1;
      if(month< 10) month = "0"+month;
      var day = String(date[2]).split(':');
      return year+"-"+month+"-"+day+" ";
    }

    getColumnTitle(date){
      date = String(date).split(' ');
      var time = String(date[1]).split(':');
      //console.log(time[0]+time[1]);
      return time[0]+":"+ time[1];
    }

    _onDeviceSelect(event){
      this.setState({
        id : event.value
      })
    }

    _onhourSelect(event){
      this.setState({
        shour : event.label,
      });
    }

    _onEndHourSelect(event){
      this.setState({
        ehour : event.label,
      });
    }

    handleDateChange = date => {
      var s = this.getParsedDate(date);
      console.log(s);
      this.setState({
        startDate : date,
        sdate : s
      });
      console.log(this.state.sdate );
    };

    _onButtonClick(event) {
      console.log("On Button Click");
      Axios.post("http://localhost:8090/getProfile",
      {
          headers: {
              'Access-Control-Allow-Origin': '*',
            },
      },
      {
        data:{
            _id:this.state.id,
            _startDate: this.state.sdate+this.state.shour+".000",
            _endDate: this.state.sdate+this.state.ehour+".000"
        }
      },
      {
          withCredentials : true
      }
      ).then(response=>{
          // console.log(response.data)
          this.setState({
            profile : response.data,
          })
          this.processResponse(response.data);
      });
        
    }


    componentDidMount() {
      Axios.get("http://localhost:8090/getDevices",
      {
          headers: {
              'Access-Control-Allow-Origin': '*',
            },
      },
      {
          withCredentials : true
      }
      ).then(response=>{
          console.log(response.data);
          this.setState({
            devices : response.data
          })
      });
    }
    //#endregion

    render() {
        return (
          <div>
            <div>
              <table>
                <tbody>
                  <tr>
                    <td><label>Device ID:  </label></td>
                    <td><span> &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></td>
                    {/* <td>
                      <Dropdown options={this.state.devices} onChange={this._onDeviceSelect} placeholder="Select an option" />
                    </td>  */}
                    <td>
                      <Select
                        onChange={this._onDeviceSelect}
                        options={this.state.devices}
                        placeholder = "Select Device"
                      />
                    </td>
                  </tr>
                  <tr>
                  <td><label>Select Date:  </label></td>
                    <td><span> &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></td>
                    <td><DatePicker selected={this.state.startDate} onChange={this.handleDateChange}/></td>
                  </tr>
                  <tr>
                    <td><label>From:  </label></td>
                    <td><span> &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></td>
                    <td>
                      {/* <Dropdown options={hours} onChange={this._onhourSelect} placeholder="Select an option" /> */}
                      <Select
                        onChange={this._onhourSelect}
                        options={hours}
                        placeholder = "Select Time"
                      />
                    </td>
                  </tr>
                  <tr>
                    <td><label> To:  </label></td>
                    <td><span> &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></td>
                    <td>
                      {/* <Dropdown options={hours} onChange={this._onEndHourSelect} placeholder="Select an option" /> */}
                      <Select
                        onChange={this._onEndHourSelect}
                        options={hours}
                        placeholder = "Select Time"
                      />
                    </td>
                  </tr>
                  <tr>
                    <td>
                      <button type="submit" onClick={this._onButtonClick} >
                            Submit
                        </button> 
                    </td>
                  </tr>
                </tbody> 
              </table>
            </div>

            {/* <div align="left">
                <DataTable
                  title="Load Profile Data"
                  columns={columns}
                  data={this.state.profile}
                  fixedHeader
                  fixedHeaderScrollHeight="300px"
                  pagination
                  onSort={handleSort}
                  sortFunction={customSort}
              />
            </div> */}

          <div>
                <div>
                  <div>
                      <h1>All Events</h1>
                      <Table cellspacing="10">
                        {/* <tr>
                            <th>Active Energy Import</th>
                            <th>Time</th>
                            <th>Channel value</th> 
                        </tr> */}
                        <thead>
                          <tr>
                            <td><th>Attribute</th></td>
                            {this.state.profile.map(event => (<td>
                                <th>{this.getColumnTitle(event._pK._dateTime)}</th>
                            </td>))}
                          </tr>
                        </thead>
                        <tbody>
                          <tr>
                            <td>Active Energy Input</td>
                            {this.state.profile.map(event => (
                                <td>{event._channelValue}</td>
                            ))}
                          </tr>
                        </tbody>
                      </Table>
                  </div>
                </div>
              </div>
          </div>
        )
      }
};

export default LPDT;
