	//#region Helper_Functions

	count_devices(){
		let count = 0;

		for(let i=0;i < this.state.segments.length;i++){
			count += this.state.segments[i]._devices.length;
		}
		return count;
	}

	toggleState() {
		this.setState({
			toggle: !this.state.toggle
		});
		console.log(this.state.toggle);
	}

	formatNumber (num) {
		return num.toString().replace(/(\d)(?=(\d{3})+(?!\d))/g, "$1,")
	}

	makeLabel(i,j){
		// if(this.state.toggle)
		// 	return this.state.power[i].PHASES[j].FORWARDACTIVEPOWER+this.state.power[i].PHASES[j].REVERSEACTIVEPOWER;
		// else 
		// 	return this.state.power[i].PHASES[j].RMSCURRENT;

		if(this.state.powerapi.length > 0){
			if(this.state.toggle)
			return this.state.powerapi[i]._phases[j]._forwardPower+this.state.powerapi[i]._phases[j]._reversePower;
		else 
			return this.state.powerapi[i]._phases[j]._rmsCurrent;
		}
	}

	makeleftLabel(i,j){
		return this.makeLabel(i-1,j)+ this.makeLabel(i+1,j);
	}

	makeParentLabel(i,j){
		return this.makeleftLabel(i-2,j)+this.makeleftLabel(i+2,j)+
			this.makeLabel(i-2,j)+this.makeLabel(i+2,j);
	}

	makeExamp(){
		console.log(this.state.segments);
		console.log(this.state.segments[1]);
		if(this.state.segments.length>1){
			console.log(this.state.segments[0]._handle);
			return this.state.segments[0]._handle;
		}
		
	}

	makeDataPoints(){
		let a = [];
		let intv = 15;
		for(let i=0;i<this.state.segments.length;i++){
			let mark = 0;
			if(this.state.segments[i]._parentHandle !== ""){
				if(i%2 === 0) {
					mark = intv*3;

					var powerBlock = {
						Label: this.state.segments[i]._power+"%", 
						color: "#E7E7E7",
						x: i,
						y: [mark+0.1,mark+1.75]
					}
					var segmentBlock ={ 
						Label: this.state.segments[i]._handle,
						color: this.state.clr[0],
						x: i,
						y: [mark+1.75,mark+4.25],
						mouseover: function(e){
							this.setState(
								{
									currentIndex : i
								}
							);
						}.bind(this),
						
					}
					var p1 ={ 
						Label: this.makeLabel(i,0),
						color: "#FFFFFF",
						x: i + 0.2,
						y: [mark+4.75,mark+6.25],
						indexLabel: "",
						indexLabelFontSize: 11,
						indexLabelFontColor : "#6D2A00"
					}
					var p2 ={ 
						Label: this.makeLabel(i,1),
						color: "#FFFFFF",
						x: i,
						y: [mark+4.75,mark+6.25],
						indexLabel: "",
						indexLabelFontSize: 11,
						indexLabelFontColor : "#2C2C2D"
					}
					var p3 ={ 
						Label: this.makeLabel(i,2),
						color: "#FFFFFF",
						x: i - 0.2,
						y: [mark+4.75,mark+6.25],
						indexLabel: "",
						indexLabelFontSize: 11,
						indexLabelFontColor : "#B1B1B9"
					}
					a.push(powerBlock,segmentBlock,p1,p2,p3);

				}
				else {
					mark = intv*2;

					powerBlock = {
						Label: this.state.segments[i]._power+"%", 
						color: "#E7E7E7",
						x: i,
						y: [mark+0.1,mark+1.75]
					}
					var childPower = {
						Label: 100-(parseInt(this.state.segments[i-1]._power)+parseInt(this.state.segments[i+1]._power))+"%",
						color: "#E7E7E7",
						x: i,
						y: [mark*(3/2)-1.75,mark*(3/2)]
					}
					segmentBlock ={ 
						Label: this.state.segments[i]._handle,
						color: this.state.clr[0],
						x: i,
						y: [mark+1.75,mark+4.25],
						mouseover: function(e){
							this.setState(
								{
									currentIndex : i
								}
							);
						}.bind(this),
						
					}
					p1 = { 
						Label: this.makeLabel(i,0),
						color: "#FFFFFF",
						x: i + 0.2,
						y: [mark+4.75,mark+6.25],
						indexLabel: "",
						indexLabelFontSize: 11,
						indexLabelFontColor : "#6D2A00"
					}
					p2 = { 
						Label: this.makeLabel(i,1),
						color: "#FFFFFF",
						x: i,
						y: [mark+4.75,mark+6.25],
						indexLabel: "",
						indexLabelFontSize: 11,
						indexLabelFontColor : "#2C2C2D"
					}
					p3 = { 
						Label: this.makeLabel(i,2),
						color: "#FFFFFF",
						x: i - 0.2,
						y: [mark+4.75,mark+6.25],
						indexLabel: "",
						indexLabelFontSize: 11,
						indexLabelFontColor : "#B1B1B9"
					}
					absorbed_p1 = { 
						Label: this.makeleftLabel(1,0),
						color: "#FFFFFF",
						x: 1.2,
						y: [41.25,42.75],
						indexLabel: "",
						indexLabelFontSize: 11,
						indexLabelFontColor : "#6D2A00"
					}
					absorbed_p2 = { 
						Label: this.makeleftLabel(1,1),
						color: "#FFFFFF",
						x: 1,
						y: [41.25,42.75],
						indexLabel: "",
						indexLabelFontSize: 11,
						indexLabelFontColor : "#2C2C2D"
					}
					absorbed_p3 = { 
						Label: this.makeleftLabel(1,2),
						color: "#FFFFFF",
						x: 0.8,
						y: [41.25,42.75],
						indexLabel: "",
						indexLabelFontSize: 11,
						indexLabelFontColor : "#B1B1B9"
					}
					a.push(powerBlock,childPower,segmentBlock,p1,p2,p3);
				}

				
			}
		}
		console.log(a);
		return a ;

	}

