									<div align ="right">
										<span>
											<form className="switch-field">
												<div className="switch-title">{""}</div>
												<input type="radio" id="switch_left" name="switchToggle"
													value={"Amp"} onChange={this.toggleState}
													checked={!this.state.toggle}/>
												<label htmlFor="switch_left">{"Amp"}</label>

												<input type="radio" id="switch_right" name="switchToggle"
													value={"Watt"} onChange={this.toggleState}
													checked={this.state.toggle}/>
												<label htmlFor="switch_right">{"Watt"}</label>
											</form>
										</span>
									</div>
									<CanvasJSChart options = {heir_options} />
								</div>
							</td>

							<td>
								<div align="left" style={divStyle2}>
									<font color='Black'>
										<div align="left"><h7>Tranformers</h7> </div>
									</font>
									<CanvasJSChart options = {options} />
									<font color='Black'>
										<div align="left"><h6>Segment {this.state.SegNo}</h6> </div>
									</font>
									<CanvasJSChart options = {segment_options} />
									<font color='Black'>
										<div align="left"><h6>Downstream Aggregate</h6> </div>
									</font>
									<CanvasJSChart options = {aggr_options} />
								</div>
							</td>
						</tr>
					</tbody>
				</table>
			</div>
		</div>
		);
	}
}

export default LineChart;
