import React,{ Component} from 'react';
import RangeBarChart from "./views/column charts/Range Bar Chart";
import {Switch, Route, Link } from 'react-router-dom';
import LineChart from './views/line charts/Line Chart';
import Login from './login.component';
import VoltageChart from './views/line charts/VoltageChart';
import LPDT from './views/Load profile table/LPDT';
import Events from './views/Events/Events';
import Axios from 'axios';
import DashBoard from './DashBoard';

class home extends Component {

  constructor(props){
    super(props)
    this.state = {
      
      segments : [],
      count: 0,
      redirect: false
    }
    this.handleClick = this.handleClick.bind(this);
  }

  count_devices(){
    var result = 0;

    for(let i = 0; i < this.state.segments.length; i++) {
      result += this.state.segments[0]._devices.length;
    }

    return result;
  }

  handleClick(e) {
    this.setState({redirect: true});
  }

  componentDidMount(){
    Axios.get(process.env.REACT_APP_SEGMENTS_API+"segments",
			{
				headers: {
					'Access-Control-Allow-Origin': '*',
					},
			},
			{
				withCredentials : true
			}
			).then(response=>{
				this.setState({
					segments : response.data
				})
			});
  }

  render() {
    return (

      <div>
        {this.state.redirect?
        <div>
          <Login/>
        </div>:
        <div>

          <div>
            <div align="left">
              <table>
                <tbody>
                  <tr>
                    <td><b><span> Transformer XXXXXXX</span></b></td>
                    <td><span> &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></td>
                    <td><span> &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></td>
                    <td><span> &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></td>
                    <td><span> &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></td>
                    <td><span> &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></td>
                    <td><span> &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></td>
                    <td><span> &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></td>
                    <td><span> &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></td>
                    <td><span> &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></td>
                    <td><span> &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></td>
                    <td><span> &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></td>
                    <td>{this.state.segments.length} Segments</td>
                    <td><span> &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></td>
                    <td>{this.count_devices()} Devices</td>
                    <td><span> &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></td>
                    <td><span> UTC+06:00 </span></td>
                    <td><span> &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></td>
                    <td><span> &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></td>
                    <td><span> &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></td>
                    <td><span> &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></td>
                    <td><span> &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></td>
                    <td><span> &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></td>
                    <td><span> &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></td>
                    <td><span> &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></td>
                    <td><span> &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></td>
                    <td><span> &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></td>
                    <td>
                      <Link to={'/navigation'}>Log Out</Link>
                      <Switch>
                        <Route exact path='/navigation' component={Login}/>
                      </Switch>
                    </td>
                  </tr>
                </tbody>
              </table>
            </div>
            {/* <Router basename={process.env.REACT_APP_ROUTER_BASE || ''}> */}
            <div>
            <nav className="navbar navbar-expand-lg navbar-light bg-light">
              <ul className="navbar-nav mr-auto">
                  <li><Link to={'/dashboard'} className="nav-link"> Topology </Link></li>
                  <li><Link to={'/topology'} className="nav-link"> Topology </Link></li>
                  <li><Link to={'/energybalance'} className="nav-link">Energy balance </Link></li>
                  <li><Link to={'/voltage'} className="nav-link">Voltage </Link></li>
                  <li><Link to={'/loadprofile'} className="nav-link">Load Profile </Link></li>
                  <li><Link to={'/events'} className="nav-link">Events</Link></li>
                  <li>&nbsp;</li>
                  <li>&nbsp;</li>
                  <li>&nbsp;</li>
                  <li>&nbsp;</li>
                  <li>&nbsp;</li>
              </ul>
            </nav>
            <Switch>
                <Route exact path='/dashboard' component={DashBoard} />
                <Route exact path='/topology' component={RangeBarChart} />
                <Route exact path ='/energybalance' component={LineChart}/>
                <Route exact path ='/voltage' component={VoltageChart}/>
                <Route exact path ='/loadprofile' component={LPDT}/>
                <Route exact path ='/events' component={Events}/>
            </Switch>
            </div>
            {/* </Router> */}
          </div>

        </div>

        }
      </div>

      );
  }
}

export default home;
