import React, { Component } from 'react';
import CanvasJSReact from '../../assets/canvasjs.react';
import { Switch, Route, Link } from 'react-router-dom';
import Login from '../../login.component';
import home from '../../home';
import Axios from 'axios';
var CanvasJSChart = CanvasJSReact.CanvasJSChart;

//#region Style
const divStyle = {
	height: '550px',
	width : '900px',
	backgroundColor: '#FFFFFF',
	resize: 'horizontal',
	overflow: 'auto'
};

const divStyle2 = {
	height: '550px',
	width: '500px',
	backgroundColor: '#383837',
	resize: 'horizontal',
	overflow: 'auto'
};

const parentdivStyle = {
	backgroundColor : '#E6E6E6'
}  

//#endregion

class RangeBarChart extends Component {

		constructor(props){
			super(props)
			this.state = {
				data : [
					{
						"SEGMENTS": [
							{
								"HANDLE": "4672",
								"PARENT": "4664",
								"DISTANCE": 10,
								"CROSSPHASEADJUSTMENT": "10",
								"VARIANCE": 40,
								"DEVICES": [
									{
										"ID": "ygqa3UlT46sdfDwac611",
										"SIGNALSTRENGTHDISCREPANCY": "10",
										"PRIMARY": []
									}
								],
								"PATH": "1/4664/4660"
							},

							{
								"HANDLE": "4660",
								"PARENT": "4664",
								"DISTANCE": 5,
								"CROSSPHASEADJUSTMENT": "10",
								"VARIANCE": 30,
								"DEVICES": [
									{
										"ID": "ygqa3UlT46sdfDwac001",
										"SIGNALSTRENGTHDISCREPANCY": "10",
										"PRIMARY": []
									}
								],
								"PATH": "1/4664"
							},

							{
								"HANDLE": "4670",
								"PARENT": "4664",
								"DISTANCE": 15,
								"CROSSPHASEADJUSTMENT": "10",
								"VARIANCE": 45,
								"DEVICES": [
									{
										"ID": "ygqa3UlT46sdfDwac691",
										"SIGNALSTRENGTHDISCREPANCY": "10",
										"PRIMARY": []
									}
								],
								"PATH": "1/4664/4660"
							},
							
							{
								"HANDLE": "4664",
								"PARENT": "",
								"DISTANCE": 25,
								"CROSSPHASEADJUSTMENT": "30",
								"VARIANCE": 25,
								"DEVICES": [
									{
										"ID": "ygqa3UlT46sdfDwac002",
										"SIGNALSTRENGTHDISCREPANCY": "10",
										"PRIMARY": []
									}
								],
								"PATH": "1"
							},

							{
								"HANDLE": "4668",
								"PARENT": "4662",
								"DISTANCE": 15,
								"CROSSPHASEADJUSTMENT": "10",
								"VARIANCE": 55,
								"DEVICES": [
									{
										"ID": "ygqa3UlT46sdfDwac129",
										"SIGNALSTRENGTHDISCREPANCY": "10",
										"PRIMARY": []
									}
								],
								"PATH": "1/4664/4662"
							},
							
							{
								"HANDLE": "4662",
								"PARENT": "4664",
								"DISTANCE": 15,
								"CROSSPHASEADJUSTMENT": "60",
								"VARIANCE": 40,
								"DEVICES": [
									{
										"ID": "ygqa3UlT46sdfDwac007",
										"SIGNALSTRENGTHDISCREPANCY": "10",
										"PRIMARY": []
									}
								],
								"PATH": "1/4664"
							},

							{
								"HANDLE": "4666",
								"PARENT": "4662",
								"DISTANCE": 10,
								"CROSSPHASEADJUSTMENT": "10",
								"VARIANCE": 50,
								"DEVICES": [
									{
										"ID": "ygqa3UlT46sdfDwac009",
										"SIGNALSTRENGTHDISCREPANCY": "10",
										"PRIMARY": []
									}
								],
								"PATH": "1/4664/4662"
							},
						],
						"UNASSIGNEDDEVICES": [
							{
								"ID": "vZyRYnQPaCimIeGCf4cdUcvnI2pYBRJr"
							}
						],
						"LVGMFUNCTIONISDISABLED": []
					}
					
				],
				segments :[],
				currentIndex: 0,
				path: "",
				SegNo: "-",
				distance: 0,
				dtt : "",
				variance : 0,
				cpa : 0,
				devices : [],
				clr : ["#FCC984","#FCC984","#FCC984","#FCC984","#FCC984","#FCC984","#FCC984"]
			}
			this.make_dataPoints = this.make_dataPoints.bind(this);
		}

		make_dataPoints(){
			let a = [];

			for(let i=0; i< this.state.segments.length;i++){
				var data = {
					Label: this.state.segments[i]._handle,
					legendText : this.state.segments[i]._variance,
					color: this.state.clr[0],
					y: [this.state.segments[i]._variance - this.state.segments[i]._distance, 
					this.state.segments[i]._distance],
					mouseover: function(e){
						this.setState(
							{
								currentIndex : i
							}
						);
					}.bind(this),
				}
				a.push(data);
			}
			console.log(a);
		}

		componentDidMount(){
			Axios.get(process.env.REACT_APP_SEGMENTS_API+"segments",
			{
				headers: {
					'Access-Control-Allow-Origin': '*',
					},
			},
			{
				withCredentials : true
			}
			).then(response=>{
				this.setState({
					segments: response.data
				})
				console.log(this.state.segments);
				this.make_dataPoints();
			});
		}

		render() {
		const options = {
			animationEnabled: true,
			dataPointMaxWidth: 12,
			title:{
				text: "",
				fontFamily: "helvetica"
			},
			subtitles: [{
				text: "",
				fontFamily: "helvetica"
			}],
			axisY: {
				title: "",
				prefix: "",
				lineThickness: 1
			},
			axisX: {
				title: "",
				prefix: "",
				lineThickness: 0,
				interval : 1,
				tickLength : 0,
				margin : 0,
				valueFormatString : " "
			},
		
			data: [{
				type: "rangeBar",
				yValueFormatString: "#0.#",
				xValueFormatStirng : "0.#",
				indexLabelFontSize: 10,
				indexLabelPlacement: "inside",  
				indexLabelOrientation: "horizontal",
				indexLabelFormatter: function(e){
					if(e.index === 0) return e.dataPoint.Label;
				  	else return e.dataPoint.legendText;
				},
				click: function(e){
					this.setState(
						{
							path : this.state.data[0].SEGMENTS[this.state.currentIndex].PATH,
							SegNo: this.state.data[0].SEGMENTS[this.state.currentIndex].HANDLE,
							distance: this.state.data[0].SEGMENTS[this.state.currentIndex].DISTANCE,
							dtt : this.state.data[0].SEGMENTS[this.state.currentIndex].VARIANCE,
							variance : this.state.data[0].SEGMENTS[this.state.currentIndex].VARIANCE,
							cpa : this.state.data[0].SEGMENTS[this.state.currentIndex].CROSSPHASEADJUSTMENT,
							devices: this.state.data[0].SEGMENTS[this.state.currentIndex].DEVICES
						}
					);
					let a = this.state.clr.slice();
					for(let i = 0; i < a.length; i++) {
						if(i === this.state.currentIndex) a[i] = "#96908E";
						else a[i] = "#FCC984";
					}
					this.setState({clr: a});
				}.bind(this),
				dataPoints: [
					{ 
						Label: this.state.data[0].SEGMENTS[0].HANDLE,
						legendText : this.state.data[0].SEGMENTS[0].VARIANCE,
						color: this.state.clr[0],
						y: [this.state.data[0].SEGMENTS[0].VARIANCE-this.state.data[0].SEGMENTS[0].DISTANCE, 
						this.state.data[0].SEGMENTS[0].VARIANCE],
						mouseover: function(e){
							this.setState(
								{
									currentIndex : 0
								}
							);
						}.bind(this),
						
					},
					{ 
						Label: this.state.data[0].SEGMENTS[1].HANDLE, 
						legendText : this.state.data[0].SEGMENTS[1].VARIANCE,
						color: this.state.clr[1],
						y: [this.state.data[0].SEGMENTS[1].VARIANCE-this.state.data[0].SEGMENTS[1].DISTANCE, 
						this.state.data[0].SEGMENTS[1].VARIANCE] ,
						mouseover: function(e){
							this.setState(
								{
									currentIndex : 1
								}
							);
						}.bind(this)
					},
					{ 
						Label: this.state.data[0].SEGMENTS[2].HANDLE, 
						legendText : this.state.data[0].SEGMENTS[2].VARIANCE,
						color: this.state.clr[2],
						y: [this.state.data[0].SEGMENTS[2].VARIANCE-this.state.data[0].SEGMENTS[2].DISTANCE, 
						this.state.data[0].SEGMENTS[2].VARIANCE],
						mouseover: function(e){
							this.setState(
								{
									currentIndex : 2
								}
							);
						}.bind(this)
					},
					{ 
						Label: this.state.data[0].SEGMENTS[3].HANDLE, 
						legendText : this.state.data[0].SEGMENTS[3].VARIANCE,
						color: this.state.clr[3],
						y: [this.state.data[0].SEGMENTS[3].VARIANCE-this.state.data[0].SEGMENTS[3].DISTANCE, 
						this.state.data[0].SEGMENTS[3].VARIANCE],
						mouseover: function(e){
							this.setState(
								{
									currentIndex : 3
								}
							);
						}.bind(this)
					},
					{ 
						Label: this.state.data[0].SEGMENTS[4].HANDLE, 
						legendText : this.state.data[0].SEGMENTS[4].VARIANCE,
						color: this.state.clr[4],
						y: [this.state.data[0].SEGMENTS[4].VARIANCE-this.state.data[0].SEGMENTS[4].DISTANCE, 
						this.state.data[0].SEGMENTS[4].VARIANCE],
						mouseover: function(e){
							this.setState(
								{
									currentIndex : 4
								}
							);
						}.bind(this)
					},
					{ 
						Label: this.state.data[0].SEGMENTS[5].HANDLE, 
						legendText : this.state.data[0].SEGMENTS[5].VARIANCE,
						color: this.state.clr[5],
						y: [this.state.data[0].SEGMENTS[5].VARIANCE-this.state.data[0].SEGMENTS[5].DISTANCE, 
						this.state.data[0].SEGMENTS[5].VARIANCE],
						mouseover: function(e){
							this.setState(
								{
									currentIndex : 5
								}
							);
						}.bind(this)
					},
					{ 
						Label: this.state.data[0].SEGMENTS[6].HANDLE, 
						legendText : this.state.data[0].SEGMENTS[6].VARIANCE,
						color: this.state.clr[6],
						y: [this.state.data[0].SEGMENTS[6].VARIANCE-this.state.data[0].SEGMENTS[6].DISTANCE, 
						this.state.data[0].SEGMENTS[6].VARIANCE],
						mouseover: function(e){
							this.setState(
								{
									currentIndex : 6
								}
							);
						}.bind(this)
					}
				
				]
			}],
		}
		
		return (
		<div>
			<div align="left"> 
              <table>
                <tbody>
                  <tr>
                    <td><b><span> Transformer XXXXXXX</span></b></td>
                    <td><span> &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></td>
                    <td><span> &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></td>
                    <td><span> &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></td>
                    <td><span> &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></td>
                    <td><span> &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></td>
                    <td><span> &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></td>
                    <td><span> &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></td>
                    <td>number Segments</td>
                    <td><span> &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></td>
                    <td>number Devices</td>
                    <td><span> &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></td>
                    <td><span> UTC+06:00 </span></td>
                    <td><span> &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></td>
                    <td><span> &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></td>
                    <td><span> &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></td>
                    <td><span> &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></td>
                    <td><span> &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></td>
                    <td><span> &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></td>
                    <td><span> &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></td>
                    <td><span> &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></td>
                    <td><span> &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></td>
                    <td><span> &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></td>
                    <td>
                      <Link to={'/navigation'}>Log Out</Link>
                      <Switch>
                        <Route exact path='/navigation' component={Login}/>
                      </Switch>
                    </td>
                  </tr>
                </tbody>
              </table>
            </div>

            <div>
            <nav className="navbar navbar-expand-lg navbar-light bg-light">
              <ul className="navbar-nav mr-auto">
                  <li><Link to={'/home'} className="nav-link">Home </Link></li>
                  <li>&nbsp;</li>
                  <li>&nbsp;</li>
                  <li>&nbsp;</li>
                  <li>&nbsp;</li>
                  <li>&nbsp;</li>
                  <li>&nbsp;</li>
              </ul>
            </nav>
            <Switch>
                <Route exact path ='/home' component={home}/>
            </Switch>
            </div>

			<div style={parentdivStyle}>
				<table>
					<tbody>
						<tr>
							<td>
								<div style={divStyle}>
									<div><span>&nbsp;&nbsp;&nbsp;&nbsp;</span></div>
									<CanvasJSChart options = {options} 
									/>
								</div>
							</td>

							<td>
								<div id="wrapper" style={divStyle2}>
									<font color={'White'}>
										<p align="left">{this.state.path}</p>
										<h3 align="left">{this.state.SegNo}<span></span></h3>
										<div align="left"><span>Distance : {this.state.distance}</span></div>
										<div align="left"><span>Distance to transformer : {this.state.dtt}</span></div>
										<div align="left"><span>Variance : {this.state.variance}</span></div>
										<div align="left"><span>Cross phase Adjustment : {this.state.cpa}</span></div>
										<div align="left"><span>&nbsp;</span> </div>
										<div align="left"><span>Devices : </span></div>

										<div align="left" >
											<div>
												<span>
													&nbsp;HP&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Serial No.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;
													&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;GHI score
												</span>
												<br/>
												{
													this.state.devices.map(function(d, idx){
														return (<span>
															&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; {d.ID} &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;
															&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;
															</span>)
													})	
												}
											</div>
										</div>
									</font>
								</div>
							</td>
						</tr>
					</tbody>
				</table>
			</div>
		</div>
		);
	}
}

export default RangeBarChart;
